SELECT 

FROM bisai_match a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.orgs = #{orgs}


		AND a.contractor = #{contractor}


		AND a.sponsors = #{sponsors}




		ORDER BY ${page.orderBy}

